INSERT INTO `company_overview_content` (`content_id`, `content`, `owned_directory`, `version`, `is_delete`, `gmt_create`, `gmt_update`) VALUES (0,'<p style=\"margin:20px 0px;\">\n                 宁沪商业保理（广州）有限公司成立于 2018 年 12 月 25 日，注册资本 9000 万元，实缴资本 9000 万元，是国有控股上市公司--江苏宁沪高速公路股份有限公司的全资子公司，注册地位于广东省广州市南沙自贸区。\n                </p>\n                <p style=\"margin:20px 0px;\">\n宁沪商业保理是宁沪高速旗下从事商业保理业务的专业化公司, 依托江苏交通控股优质商业信用资源，秉承不断开拓进取的创新理念，实现传统产业与互联网、与金融的相互融合，在健全的风控体系 基础上为成员企业与上下游企业提供可信赖、最便捷、最专业的供应 链金融服务。\n                </p>\n                <p style=\"margin:20px 0px;\">\n          公司通过积极探索产业金融领域创新模式，为江苏高速公路产业链链属企业，科学、规范、高效地解决经营资金周转问题。借助旗下“苏高速产业金融平台”，为江苏交通控股产业链链属企业提供量身定制的全在线保理业务，携手上下游供应商伙伴，以金融科技创新共迎供应链金融结算全新时代，实现核心企业、供应商和保理公司成长共赢的愿景。\n               </p>',0,7,0,'2021-01-12 06:16:44','2021-01-14 07:34:37'),(1,'<p style=\"margin:20px 0px;\">\n               江苏宁沪高速公路股份有限公司成立于1992年8月，注册资本人民币50.38亿元，是江苏省唯一的交通基建类上市公司。 </p>\n                <p style=\"margin:20px 0px;\">\n集团主要从事投资、建设、经营和管理沪宁高速公路及本集团拥有或参股的江苏省境内的收费公路，并发展该等公路沿线的客运及其他辅助服务业（包括加油、餐饮、购物、汽车维修、广告及住宿等）。    </p>\n                <p style=\"margin:20px 0px;\">\n        集团核心业务是收费路桥的投资、建设、营运和管理。除沪宁高速公路外，还拥有宁连公路南京段、锡澄高速公路、广靖高速公路、江阴长江公路大桥以及苏嘉杭高速公路等位于江苏省内的收费路桥全部或部分权益。截至2019年12月31日，集团直接参与经营和投资的路桥项目达到17个，直接或间接拥有的已开通公路里程已超过840公里      </p>',1,1,0,'2021-01-12 06:17:16','2021-01-14 07:34:08'),(2,'<img width=\"380px\" style=\"margin-left:100px\" src=\"https://axhub.im/ax9/71d086cb448a7a75/images/%E5%85%AC%E5%8F%B8%E6%A6%82%E5%86%B5/u75.jpg\" alt=\"\">',2,1,0,'2021-01-12 06:17:56','2021-01-14 07:34:37'),(3,'<img width=\"380px\" style=\"margin-left:100px\" src=\"https://axhub.im/ax9/71d086cb448a7a75/images/%E5%85%AC%E5%8F%B8%E6%A6%82%E5%86%B5/u75.jpg\" alt=\"\">',3,1,0,'2021-01-14 07:16:55','2021-01-14 07:35:27');
/*!40000 ALTER TABLE `company_overview_content` ENABLE KEYS */;
UNLOCK TABLES;

