INSERT INTO `recruitment` (`recruitment_id`, `title`, `content`, `version`, `is_delete`, `gmt_create`, `gmt_update`) VALUES (1,'2020年1月份招聘','<div class=\"TRS_PreAppend\" style=\"overflow-x: hidden; word-break: break-all\">\n<p class=\"MsoNormal\" style=\"layout-grid-mode: char; line-height: 150%; text-autospace: ideograph-numeric; text-indent: 24.1pt\"><b><span style=\"font-size: 12pt; font-family: 宋体; font-weight: bold; color: rgb(0,0,0); line-height: 150%\"><font face=\"宋体\">一、单位简介</font></span></b></p>\n<p class=\"MsoNormal\" style=\"layout-grid-mode: char; line-height: 150%; text-autospace: ideograph-numeric; text-indent: 24pt\"><span style=\"font-size: 12pt; font-family: 宋体; line-height: 150%\"><font face=\"宋体\">国家基础地理信息中心为自然资源部所属公益二类事业单位，主要职责是：承担全国基础测绘和重大测绘工程的规划设计与组织实施；负责国家大地基准和国家基础地理信息系统建设、维护与更新；承担全球地理信息资源建设与开发工作；承担自然资源专项调查监测工作；负责地理信息公共服务平台建设与维护；承担应急测绘保障服务工作；负责管理分发全国测绘成果和档案资料等。</font></span></p>\n<p class=\"MsoNormal\" style=\"layout-grid-mode: char; line-height: 150%; text-autospace: ideograph-numeric; text-indent: 24.1pt\"><b><span style=\"font-size: 12pt; font-family: 宋体; font-weight: bold; color: rgb(0,0,0); line-height: 150%\"><font face=\"宋体\">二、招聘岗位</font></span></b></p>\n<div align=\"center\">\n<table class=\"MsoNormalTable\" style=\"border-top: medium none; border-right: medium none; width: 574.85pt; border-collapse: collapse; border-bottom: medium none; border-left: medium none\" border=\"1\" cellspacing=\"0\">\n    <tbody>\n        <tr style=\"height: 39.3pt\">\n            <td width=\"38\" valign=\"middle\" style=\"border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; width: 29.15pt; border-bottom: windowtext 1pt solid; padding-bottom: 0pt; padding-top: 0pt; padding-left: 5.4pt; border-left: windowtext 1pt solid; padding-right: 5.4pt\">\n            <p align=\"center\" class=\"MsoNormal\" style=\"layout-grid-mode: char; text-align: center; line-height: 150%; text-autospace: ideograph-numeric; text-indent: 0pt\"><span style=\"font-s